def runner_setup():
    import time
    import numpy as np
    import pyopencl as cl

    # Add the kernel code into a python string
    opencl_code = """
    __kernel void matmul_adreno_optimized(__global const float* restrict A, 
                                         __global const float* restrict B, 
                                         __global float* restrict C, 
                                         const int N) {
        // Define tile size (tuned for Adreno)
        const int TILE_SIZE = 16;

        // Local memory for tiles
        __local float A_tile[TILE_SIZE][TILE_SIZE];
        __local float B_tile[TILE_SIZE][TILE_SIZE];

        // Thread coordinates within work-group
        const int tx = get_local_id(0);
        const int ty = get_local_id(1);

        // Output coordinates
        const int row = get_global_id(1);
        const int col = get_global_id(0);

        // Early out if out of bounds
        if (row >= N || col >= N) {
            return;
        }

        // Accumulator in register
        float acc = 0.0f;

        // Loop over tiles
        const int num_tiles = (N + TILE_SIZE - 1) / TILE_SIZE;

        for (int t = 0; t < num_tiles; t++) {
            // Load tiles collaboratively
            const int tile_offset = t * TILE_SIZE;

            // A tile element position
            const int a_row = row;
            const int a_col = tile_offset + tx;

            // B tile element position
            const int b_row = tile_offset + ty;
            const int b_col = col;

            // Load elements to local memory with boundary checks
            A_tile[ty][tx] = (a_col < N) ? A[a_row * N + a_col] : 0.0f;
            B_tile[ty][tx] = (b_row < N) ? B[b_row * N + b_col] : 0.0f;

            // Ensure all threads have loaded their elements
            barrier(CLK_LOCAL_MEM_FENCE);

            // Multiply and accumulate with unrolling for performance
            #pragma unroll 4
            for (int k = 0; k < TILE_SIZE; k++) {
                acc += A_tile[ty][k] * B_tile[k][tx];
            }

            // Ensure computation is done before loading next tile
            barrier(CLK_LOCAL_MEM_FENCE);
        }

        // Store result
        C[row * N + col] = acc;
    }
    """

    dim = 2048
    num_iterations = 10
    block_size = [16, 16]
    grid_size = [(dim + block_size[0] - 1) // block_size[0], (dim + block_size[1] - 1) // block_size[1]]

    # Function to verify the results
    def verification_fn():
        result_host = np.empty((dim, dim), dtype=np.float32)
        cl.enqueue_copy(queue, result_host, C_buf)
        expected = np.matmul(A_host, B_host)
        ok = np.allclose(result_host, expected, rtol=1e-5, atol=1e-5)
        return ok, "Results match!" if ok else "Results do NOT match!"

    # Initialize data with random values
    A_host = np.random.rand(dim, dim).astype(np.float32)
    B_host = np.random.rand(dim, dim).astype(np.float32)

    # Create OpenCL buffers
    mf = cl.mem_flags
    platforms = cl.get_platforms()
    if not platforms:
        raise RuntimeError("No OpenCL platforms found.")
    platform = platforms[0]
    device = platform.get_devices()[0]
    print(f"Using OpenCL device: {device.name}")
    ctx = cl.Context([device])
    queue = cl.CommandQueue(ctx)

    A_buf = cl.Buffer(ctx, mf.READ_ONLY | mf.COPY_HOST_PTR, hostbuf=A_host)
    B_buf = cl.Buffer(ctx, mf.READ_ONLY | mf.COPY_HOST_PTR, hostbuf=B_host)
    C_buf = cl.Buffer(ctx, mf.WRITE_ONLY, size=dim * dim * np.dtype(np.float32).itemsize)

    # Build the kernel
    program = cl.Program(ctx, opencl_code).build()
    matmul_kernel = getattr(program, "matmul_adreno_optimized")

    execution_times = []
    for i in range(num_iterations):
        # Get kernel inputs and grid size
        args = (A_buf, B_buf, C_buf, np.int32(dim))

        # Normalise grid / block specification to sequences
        global_size = [dim, dim]  # Global size corresponds to full matrix
        local_size = block_size   # Local size is our block size

        queue.finish()
        start_time = time.time()

        # Launch kernel
        matmul_kernel(queue, global_size, local_size, *args)

        queue.finish()
        end_time = time.time()
        elapsed_time = (end_time - start_time) * 1000

        execution_times.append(elapsed_time)
        print(f"Iteration {i+1}: {elapsed_time:.2f} ms")

    avg_time = sum(execution_times) / len(execution_times)
    min_time = min(execution_times)
    max_time = max(execution_times)

    is_correct, msg = verification_fn() if verification_fn else (None, None)

    timing_result = {
        "iterations": num_iterations,
        "average_ms": avg_time,
        "min_ms": min_time,
        "max_ms": max_time,
        "all_times_ms": execution_times,
        "correct_result": is_correct,
        "verification_feedback": msg,
        "block_size": block_size,
        "grid_size": grid_size
    }
    return timing_result
