def runner_setup():
    import time
    import numpy as np
    import pyopencl as cl

    # Kernel code
    opencl_code = """
    __kernel void matmul_kernel(__global const float* restrict A,
                                __global const float* restrict B,
                                __global float* restrict C,
                                const int N) {
        // Define tile size
        const int TS = 16;

        // Local memory tiles
        __local float A_tile[TS][TS];
        __local float B_tile[TS][TS];

        // Get global position
        const int row = get_global_id(0);
        const int col = get_global_id(1);

        // Local IDs
        const int local_row = get_local_id(0);
        const int local_col = get_local_id(1);

        // Accumulator
        float sum = 0.0f;

        // Loop over all tiles
        for (int t = 0; t < (N + TS - 1) / TS; ++t) {
            // Load A tile
            if (row < N && t * TS + local_col < N) {
                A_tile[local_row][local_col] = A[row * N + t * TS + local_col];
            } else {
                A_tile[local_row][local_col] = 0.0f;
            }

            // Load B tile
            if (t * TS + local_row < N && col < N) {
                B_tile[local_row][local_col] = B[(t * TS + local_row) * N + col];
            } else {
                B_tile[local_row][local_col] = 0.0f;
            }

            // Wait until all work-items have loaded their tiles
            barrier(CLK_LOCAL_MEM_FENCE);

            // Perform computation for this tile
            #pragma unroll 4
            for (int k = 0; k < TS; ++k) {
                sum += A_tile[local_row][k] * B_tile[k][local_col];
            }

            // Synchronize before loading next tile
            barrier(CLK_LOCAL_MEM_FENCE);
        }

        // Write result
        if (row < N && col < N) {
            C[row * N + col] = sum;
        }
    }
    """

    dim = 2048
    num_iterations = 10
    tile_size = 16
    block_size = (tile_size, tile_size)
    grid_size = ((dim + tile_size - 1) // tile_size * tile_size, 
                 (dim + tile_size - 1) // tile_size * tile_size)

    # Function to verify the results
    def verification_fn():
        result_host = np.empty((dim, dim), dtype=np.float32)
        cl.enqueue_copy(queue, result_host, C_buf)
        expected = np.matmul(A_host, B_host)
        ok = np.allclose(result_host, expected, rtol=1e-5, atol=1e-5)
        return ok, "Results match!" if ok else "Results do NOT match!"

    # Initialize data with random values
    A_host = np.random.rand(dim, dim).astype(np.float32)
    B_host = np.random.rand(dim, dim).astype(np.float32)

    # Create OpenCL buffers
    mf = cl.mem_flags
    platforms = cl.get_platforms()
    if not platforms:
        raise RuntimeError("No OpenCL platforms found.")
    platform = platforms[0]
    devices = platform.get_devices()
    device = devices[0]
    print(f"Using OpenCL device: {device.name}")
    ctx = cl.Context([device])
    queue = cl.CommandQueue(ctx)

    A_buf = cl.Buffer(ctx, mf.READ_ONLY | mf.COPY_HOST_PTR, hostbuf=A_host)
    B_buf = cl.Buffer(ctx, mf.READ_ONLY | mf.COPY_HOST_PTR, hostbuf=B_host)
    C_buf = cl.Buffer(ctx, mf.WRITE_ONLY, size=dim * dim * np.dtype(np.float32).itemsize)

    # Build the kernel
    program = cl.Program(ctx, opencl_code).build()
    matmul = program.matmul_kernel

    execution_times = []
    for i in range(num_iterations):
        # Set up arguments
        args = (A_buf, B_buf, C_buf, np.int32(dim))

        # Wait for previous operations to complete
        queue.finish()

        # Time kernel execution
        start_time = time.time()
        matmul(queue, grid_size, block_size, *args)
        queue.finish()
        end_time = time.time()

        # Calculate elapsed time in milliseconds
        elapsed_time = (end_time - start_time) * 1000
        execution_times.append(elapsed_time)
        print(f"Iteration {i+1}: {elapsed_time:.2f} ms")

    # Calculate statistics
    avg_time = sum(execution_times) / len(execution_times)
    min_time = min(execution_times)
    max_time = max(execution_times)

    # Verify the results
    is_correct, msg = verification_fn()

    timing_result = {
        "iterations": num_iterations,
        "average_ms": avg_time,
        "min_ms": min_time,
        "max_ms": max_time,
        "all_times_ms": execution_times,
        "correct_result": is_correct,
        "verification_feedback": msg,
        "block_size": block_size,
        "grid_size": grid_size
    }
    return timing_result
