def runner_setup():
    import time
    import numpy as np
    import pyopencl as cl

    # Add the kermnel code into a python string
    opencl_code = """
    #define TILE_SIZE 16

    __kernel void matmul(__global const float* restrict A,
                         __global const float* restrict B,
                         __global float* restrict C,
                         const int N) {
        __local float Asub[TILE_SIZE][TILE_SIZE];
        __local float Bsub[TILE_SIZE][TILE_SIZE];

        // Block indices
        const int bx = get_group_id(0);
        const int by = get_group_id(1);

        // Thread indices within a block
        const int tx = get_local_id(0);
        const int ty = get_local_id(1);

        // Global row and column indices for the thread
        const int row = by * TILE_SIZE + ty;
        const int col = bx * TILE_SIZE + tx;

        float sum = 0.0f;

        // Loop over all tiles
        const int numTiles = (N + TILE_SIZE - 1) / TILE_SIZE;

        for (int t = 0; t < numTiles; t++) {
            // Load one tile of A and one tile of B into local memory
            const int tileRow = row;
            const int tileCol = t * TILE_SIZE + tx;

            if (tileRow < N && tileCol < N)
                Asub[ty][tx] = A[tileRow * N + tileCol];
            else
                Asub[ty][tx] = 0.0f;

            const int tileRow2 = t * TILE_SIZE + ty;
            const int tileCol2 = col;

            if (tileRow2 < N && tileCol2 < N)
                Bsub[ty][tx] = B[tileRow2 * N + tileCol2];
            else
                Bsub[ty][tx] = 0.0f;

            // Synchronize to make sure the tile is loaded
            barrier(CLK_LOCAL_MEM_FENCE);

            // Compute the partial result for this tile
            #pragma unroll
            for (int k = 0; k < TILE_SIZE; k++) {
                sum += Asub[ty][k] * Bsub[k][tx];
            }

            // Synchronize to avoid loading the next tile before computation is done
            barrier(CLK_LOCAL_MEM_FENCE);
        }

        // Write the result
        if (row < N && col < N)
            C[row * N + col] = sum;
    }
    """

    dim = 2048  # Matrix size
    num_iterations = 10  # Number of iterations
    block_size = (16, 16)  # Workgroup/block size
    grid_size = ((dim + block_size[0] - 1) // block_size[0], (dim + block_size[1] - 1) // block_size[1])  # Number of workgroups/blocks

    # Function to verify the results
    def verification_fn():
        result_host = np.empty((dim, dim), dtype=np.float32)
        cl.enqueue_copy(queue, result_host, C_buf)
        expected = np.matmul(A_host, B_host)
        ok = np.allclose(result_host, expected, rtol=1e-4, atol=1e-4)
        return ok, "Results match!" if ok else "Results do NOT match!"

    # Initialize data with random values
    A_host = np.random.rand(dim, dim).astype(np.float32)
    B_host = np.random.rand(dim, dim).astype(np.float32)

    # Create OpenCL buffers
    mf = cl.mem_flags
    platforms = cl.get_platforms()
    if not platforms:
        raise RuntimeError("No OpenCL platforms found.")
    platform = platforms[0]
    device = platform.get_devices()[0]
    print(f"Using OpenCL device: {device.name}")
    ctx = cl.Context([device])
    queue = cl.CommandQueue(ctx)

    A_buf = cl.Buffer(ctx, mf.READ_ONLY | mf.COPY_HOST_PTR, hostbuf=A_host)
    B_buf = cl.Buffer(ctx, mf.READ_ONLY | mf.COPY_HOST_PTR, hostbuf=B_host)
    C_buf = cl.Buffer(ctx, mf.WRITE_ONLY, size=dim * dim * np.dtype(np.float32).itemsize)

    # Build the kernel
    program = cl.Program(ctx, opencl_code).build()
    matmul_kernel = getattr(program, "matmul")

    execution_times = []
    for i in range(num_iterations):
        # Get kernel inputs and grid size from the setup function
        args = (A_buf, B_buf, C_buf, np.int32(dim))
        # Calculate global and local sizes
        global_size = (grid_size[0] * block_size[0], grid_size[1] * block_size[1])
        local_size = block_size

        queue.finish()
        start_time = time.time()
        # Launch kernel
        matmul_kernel(queue, global_size, local_size, *args)
        queue.finish()
        end_time = time.time()
        elapsed_time = (end_time - start_time) * 1000

        execution_times.append(elapsed_time)
        print(f"Iteration {i+1}: {elapsed_time:.2f} ms")

    avg_time = sum(execution_times) / len(execution_times)
    min_time = min(execution_times)
    max_time = max(execution_times)

    is_correct, msg = verification_fn() if verification_fn else (None, None)

    timing_result = {
        "iterations": num_iterations,
        "average_ms": avg_time,
        "min_ms": min_time,
        "max_ms": max_time,
        "all_times_ms": execution_times,
        "correct_result": is_correct,
        "verification_feedback": msg,
        "block_size": block_size,
        "grid_size": grid_size
    }
    return timing_result
