def runner_setup():
    import time
    import numpy as np
    import pyopencl as cl

    # OpenCL kernel code
    opencl_code = """
    #define TILE_SIZE 32

    __kernel void matmul(__global const float* restrict A,
                       __global const float* restrict B,
                       __global float* restrict C,
                       const int N) {

        // Allocate local memory for tiles
        __local float A_tile[TILE_SIZE][TILE_SIZE];
        __local float B_tile[TILE_SIZE][TILE_SIZE];

        // Get global position coordinates
        const int row = get_global_id(1);
        const int col = get_global_id(0);

        // Get local position within the workgroup
        const int local_row = get_local_id(1);
        const int local_col = get_local_id(0);

        // Initialize accumulator
        float sum = 0.0f;

        // Calculate number of tiles required
        const int numTiles = (N + TILE_SIZE - 1) / TILE_SIZE;

        for (int t = 0; t < numTiles; t++) {
            // Load tiles into local memory
            const int tileCol = TILE_SIZE * t + local_col;
            const int tileRow = TILE_SIZE * t + local_row;

            // Load A tile - check bounds
            if (row < N && tileCol < N) {
                A_tile[local_row][local_col] = A[row * N + tileCol];
            } else {
                A_tile[local_row][local_col] = 0.0f;
            }

            // Load B tile - check bounds
            if (tileRow < N && col < N) {
                B_tile[local_row][local_col] = B[tileRow * N + col];
            } else {
                B_tile[local_row][local_col] = 0.0f;
            }

            // Ensure all work-items have loaded their data
            barrier(CLK_LOCAL_MEM_FENCE);

            // Compute dot product for this tile
            #pragma unroll 8
            for (int k = 0; k < TILE_SIZE; k++) {
                sum += A_tile[local_row][k] * B_tile[k][local_col];
            }

            // Ensure all computations are complete before loading next tile
            barrier(CLK_LOCAL_MEM_FENCE);
        }

        // Write result to global memory if within bounds
        if (row < N && col < N) {
            C[row * N + col] = sum;
        }
    }
    """

    # Matrix size
    N = 2048
    num_iterations = 5  # Number of timing iterations

    # Initialize data with random values
    A_host = np.random.rand(N, N).astype(np.float32)
    B_host = np.random.rand(N, N).astype(np.float32)
    C_host = np.zeros((N, N), dtype=np.float32)

    # Function to verify the results
    def verification_fn():
        result_host = np.empty_like(C_host)
        cl.enqueue_copy(queue, result_host, C_buf)
        expected = np.matmul(A_host, B_host)
        ok = np.allclose(result_host, expected, rtol=1e-4, atol=1e-4)
        return ok, "Results match!" if ok else "Results do NOT match!"

    # Setup OpenCL
    platforms = cl.get_platforms()
    if not platforms:
        raise RuntimeError("No OpenCL platforms found.")

    platform = platforms[0]
    devices = platform.get_devices(device_type=cl.device_type.GPU)
    if not devices:
        devices = platform.get_devices()

    device = devices[0]
    print(f"Using OpenCL device: {device.name}")
    ctx = cl.Context([device])
    queue = cl.CommandQueue(ctx)

    # Create buffers
    mf = cl.mem_flags
    A_buf = cl.Buffer(ctx, mf.READ_ONLY | mf.COPY_HOST_PTR, hostbuf=A_host)
    B_buf = cl.Buffer(ctx, mf.READ_ONLY | mf.COPY_HOST_PTR, hostbuf=B_host)
    C_buf = cl.Buffer(ctx, mf.WRITE_ONLY, C_host.nbytes)

    # Build the program and get the kernel
    program = cl.Program(ctx, opencl_code).build()
    matmul_kernel = program.matmul

    # Define global and local work sizes
    tile_size = 32
    global_size = (((N + tile_size - 1) // tile_size) * tile_size, ((N + tile_size - 1) // tile_size) * tile_size)
    local_size = (tile_size, tile_size)

    # Time the kernel execution
    execution_times = []
    for i in range(num_iterations):
        # Warmup run
        if i == 0:
            matmul_kernel(queue, global_size, local_size, A_buf, B_buf, C_buf, np.int32(N))
            queue.finish()

        # Timed run
        queue.finish()
        start_time = time.time()
        matmul_kernel(queue, global_size, local_size, A_buf, B_buf, C_buf, np.int32(N))
        queue.finish()
        end_time = time.time()

        elapsed_time = (end_time - start_time) * 1000  # Convert to milliseconds
        execution_times.append(elapsed_time)
        print(f"Iteration {i+1}: {elapsed_time:.2f} ms")

    # Calculate statistics
    avg_time = sum(execution_times) / len(execution_times)
    min_time = min(execution_times)
    max_time = max(execution_times)

    # Verify results
    is_correct, msg = verification_fn()

    # Return timing results
    timing_result = {
        "iterations": num_iterations,
        "average_ms": avg_time,
        "min_ms": min_time,
        "max_ms": max_time,
        "all_times_ms": execution_times,
        "correct_result": is_correct,
        "verification_feedback": msg,
        "local_size": local_size,
        "global_size": global_size
    }

    return timing_result
