    import time
    import numpy as np
    import pyopencl as cl

    # Add the kernel code into a python string
    opencl_code = """__kernel void matmul_kernel(__global const float* restrict A,
                          __global const float* restrict B,
                          __global float* restrict C,
                          const int N) {
    // Define tile size - optimized for NVIDIA RTX 4070 architecture
    const int TILE_SIZE = 32;

    // Allocate shared memory for tiles
    __local float A_tile[TILE_SIZE][TILE_SIZE];
    __local float B_tile[TILE_SIZE][TILE_SIZE];

    // Thread identifiers
    const int tx = get_local_id(0);
    const int ty = get_local_id(1);
    const int row = get_group_id(1) * TILE_SIZE + ty;
    const int col = get_group_id(0) * TILE_SIZE + tx;

    // Initialize accumulator
    float sum = 0.0f;

    // Loop over tiles
    const int numTiles = (N + TILE_SIZE - 1) / TILE_SIZE;

    for (int t = 0; t < numTiles; ++t) {
        // Collaborative loading of tiles
        if (row < N && t * TILE_SIZE + tx < N) {
            A_tile[ty][tx] = A[row * N + t * TILE_SIZE + tx];
        } else {
            A_tile[ty][tx] = 0.0f;
        }

        if (t * TILE_SIZE + ty < N && col < N) {
            B_tile[ty][tx] = B[(t * TILE_SIZE + ty) * N + col];
        } else {
            B_tile[ty][tx] = 0.0f;
        }

        barrier(CLK_LOCAL_MEM_FENCE);

        // Computation - unrolled loop for better performance
        #pragma unroll 8
        for (int k = 0; k < TILE_SIZE; ++k) {
            sum += A_tile[ty][k] * B_tile[k][tx];
        }

        barrier(CLK_LOCAL_MEM_FENCE);
    }

    // Write result
    if (row < N && col < N) {
        C[row * N + col] = sum;
    }
}"""

    # Define constants
    N = 2048
    num_iterations = 10

    # Initialize data with random values
    A_host = np.random.rand(N, N).astype(np.float32)
    B_host = np.random.rand(N, N).astype(np.float32)
    C_host = np.zeros((N, N), dtype=np.float32)

    # Function to verify the results
    def verification_fn():
        result_host = np.empty_like(C_host)
        cl.enqueue_copy(queue, result_host, C_buf)
        expected = np.matmul(A_host, B_host)
        ok = np.allclose(result_host, expected, rtol=1e-4, atol=1e-4)
        return ok, "Results match!" if ok else "Results do NOT match!"

    # Create OpenCL context, queue, and buffers
    platforms = cl.get_platforms()
    if not platforms:
        raise RuntimeError("No OpenCL platforms found.")

    platform = platforms[0]
    devices = platform.get_devices()
    if not devices:
        raise RuntimeError("No OpenCL devices found.")

    device = devices[0]
    print(f"Using OpenCL device: {device.name}")

    ctx = cl.Context([device])
    queue = cl.CommandQueue(ctx, properties=cl.command_queue_properties.PROFILING_ENABLE)

    mf = cl.mem_flags
    A_buf = cl.Buffer(ctx, mf.READ_ONLY | mf.COPY_HOST_PTR, hostbuf=A_host)
    B_buf = cl.Buffer(ctx, mf.READ_ONLY | mf.COPY_HOST_PTR, hostbuf=B_host)
    C_buf = cl.Buffer(ctx, mf.WRITE_ONLY, size=N * N * np.dtype(np.float32).itemsize)

    # Build the kernel
    program = cl.Program(ctx, opencl_code).build("-cl-mad-enable -cl-fast-relaxed-math")
    matmul = program.matmul_kernel

    # Define local and global work sizes
    local_size = (32, 32)  # TILE_SIZE x TILE_SIZE threads per work group
    global_size = ((N + local_size[0] - 1) // local_size[0] * local_size[0], 
                  (N + local_size[1] - 1) // local_size[1] * local_size[1])

    # Create kernel arguments
    kernel_args = (A_buf, B_buf, C_buf, np.int32(N))

    # Warmup run
    matmul(queue, global_size, local_size, *kernel_args)
    queue.finish()

    # Time execution
    execution_times = []
    for i in range(num_iterations):
        queue.finish()
        start_time = time.time()
        event = matmul(queue, global_size, local_size, *kernel_args)
        event.wait()
        queue.finish()
        end_time = time.time()
        elapsed_time = (end_time - start_time) * 1000  # Convert to ms
        execution_times.append(elapsed_time)
        print(f"Iteration {i+1}: {elapsed_time:.2f} ms")

    # Calculate statistics
    avg_time = sum(execution_times) / len(execution_times)
    min_time = min(execution_times)
    max_time = max(execution_times)

    # Verify results
    is_correct, msg = verification_fn()

    # Return timing results
    timing_result = {
        "iterations": num_iterations,
        "average_ms": avg_time,
        "min_ms": min_time,
        "max_ms": max_time,
        "all_times_ms": execution_times,
        "correct_result": is_correct,
        "verification_feedback": msg,
        "global_size": global_size,
        "local_size": local_size
